
public class TableTen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 10;
		System.out.println("table of "+number+ "is:");
		for (int i =1;i<=number;i++) {
			int res = number*i;
			System.out.println(number + " x " + i + " = " + res);
		}

	}

}
